namespace AbraFlexi\Contractor\Ui;

use AbraFlexi\Contractor\Contract;
use AbraFlexi\Contractor\UndefinedSummary;
use ByJG\JinjaPhp\Template;
use Ease\TWB5\Form;

class SummaryForm extends Form
{
    private Contract $contract;

    public function __construct(Contract $contract, bool $printMode = false)
    {
        parent::__construct();
        $this->contract = $contract;

        $templateString = file_get_contents('../templates/summary.html.j2');

        $template = new Template($templateString);
        $template->withUndefined(new UndefinedSummary($contract));  // Default is StrictUndefined

        $contractData = ['contract' => $contract->getData()];

        $this->addItem($template->render($contractData));

        if ($printMode === false) {
            $buttons = new \Ease\TWB5\Navbar($contract->getRecordIdent());
            $buttons->addTagClass('navbar-expand-lg navbar-light bg-dark');
            $buttons->addMenuItem(new \Ease\Html\DivTag(new \Ease\TWB5\LinkButton('summary.php?format=html&kod='.$contract->getRecordIdent(), '🖨️&nbsp;'._('Print Contract Summary'), 'success', ['target' => '_blank']), ['class' => '']));
            $buttons->addMenuItem(new \Ease\Html\DivTag(new \Ease\TWB5\LinkButton('summary.php?format=pdf&kod='.$contract->getRecordIdent(), '<img height="20" src="data:image/svg+xml;base64,">️&nbsp;'._('Download Contract Summary PDF'), 'success', ['target' => '_blank']), ['class' => '']));
            $buttons->addMenuItem(new \Ease\Html\DivTag(new \Ease\TWB5\LinkButton('summary.php?format=docx&kod='.$contract->getRecordIdent(), '<img height="20" src="data:image/svg+xml;base64,">️&nbsp;'._('Open Contract Summary in Word'), 'success', ['target' => '_blank']), ['class' => '']));
            $this->addItem($buttons);
        }
    }
}
